package _09_loops;

public class _01_While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int count = 0;
		while (count <=10){
			System.out.println(count);
			count++;
		}
		
		int countTwo = 10;
		while (countTwo>=0){
			System.out.println(countTwo);
			countTwo--;
		}
			
		int countThree=100;
		while (countThree>=0){
			System.out.println(countThree);
			countThree-=5;
		}
	}
}


